package com.xiaopengwei.algorithm.check;import java.util.Stack;/** * <p> * * @author XiaoPengwei * @since 2019-05-15 */public class ValidCheck {    public boolean isMatch(String s) {        Stack<Character> sc = new Stack<Character>();        char[] c = s.toCharArray();        for (int i = 0; i < c.length; i++) {            if (c[i] == '(' || c[i] == '[' || c[i] == '{') {                sc.push(c[i]);            } else if (c[i] == ')') {                if (sc.isEmpty()) {                    return false;                } else {                    if (sc.peek() == '(') {                        sc.pop();                    }                }            } else if (c[i] == ']') {                if (sc.isEmpty()) {                    return false;                } else {                    if (sc.peek() == '[') {                        sc.pop();                    }                }            } else if (c[i] == '}') {                if (sc.isEmpty()) {                    return false;                } else {                    if (sc.peek() == '{') {                        sc.pop();                    }                }            }        }        if (sc.empty()) {            return true;        } else {            return false;        }    }    public static void main(String[] args) {        ValidCheck validatorMethod = new ValidCheck();        String str = "(((999+666)*5/7)/2*((996+997)*30/7)/2)";        boolean bool = validatorMethod.isMatch(str);        System.out.println(bool);    }}