package com.xiaopengwei.thread.t1.chreateby3ways;/** * <p> * Runnable 接口的实现 * </p> * * @author xpwi * @since 2019-04-18 */public class T04ThreadByImplemetnsRunable implements Runnable {    private int i;    @Override    public void run() {        for (;i<20;i++){            System.out.println("第"+i+"个 Thread："+Thread.currentThread().getName());        }    }}