package com.xiaopengwei.thread.t1.chreateby3ways;/** * <p> * 测试 * </p> * * @author XiaoPengwei.com * @since 2019-04-18 */public class ThreadByExtendsThreadTest {    public static void main(String[] args) {        for (int i = 0; i < 10; i++) {            System.out.println("主线程执行-->第" + i + "次：" + Thread.currentThread().getName());            if (i == 5) {                System.out.println("i == 5 启动线程-->");                T02ThreadByExtendsThread myThread01 = new T02ThreadByExtendsThread("Thread-xiao");                T02ThreadByExtendsThread myThread02 = new T02ThreadByExtendsThread();                myThread01.start();                myThread02.start();            }        }        System.out.println("\n结果：\n(1)当 i==5 时启动2个线程, 并没有立即执行打印");        System.out.println("(2)这两个线程是交替执行的, 并且每次执行的顺序都不一样");        System.out.println("(3)顺序是由 CPU 决定的\n----------------------------");    }}