package com.xiaopengwei.thread.t3.synchronizeSafe;/** * <p> * 调用线程测试登录 * * @author XiaoPengwei * @since 2019-04-24 */public class SynchronizedLoginTest {    public static void main(String[] args) {        UserALoginThread userALoginThread = new UserALoginThread();        userALoginThread.start();        UserBLoginThread userBLoginThread = new UserBLoginThread();        userBLoginThread.start();        System.out.println("同步锁安全\n多次运行会出现密码和用户一直是一致的\n");    }}