package com.xiaopengwei.thread.t7;/** * <p> * * @author XiaoPengwei * @since 2019-04-24 */public class MyThreadInterrupt {    public static void main(String[] args) throws InterruptedException {        MyThread myThread = new MyThread();        System.out.println("before start"+myThread.isAlive());        myThread.start();        System.out.println("after start"+myThread.isAlive());        myThread.interrupt();        System.out.println("after interrupt: " + myThread.isAlive());        System.out.println("会发现执行了 interrupt(), 并没有打断, 很容易看出来此时还没有执行 run()");        Thread.sleep(2000);        myThread.interrupt();        System.out.println("After sleep interrupt();"+myThread.isAlive());    }}