package com.xpwi.thread.t3.synchronizeSafe;/** * <p> *  不安全 * * @author xpwi * @since 2019-04-24 */public class SynchronizedLoginServlet {    private static String usernameRef;    private static String passwordRef;    synchronized public static void doPost(String username, String password){        try{            usernameRef = username;            if (username.equals("userA")){                Thread.sleep(3000);            }            passwordRef = password;            System.out.println("username="+usernameRef+" password="+passwordRef);        } catch (InterruptedException e) {            e.printStackTrace();        }    }}